import React, { useState } from 'react';

const Contador = () => {
  const [cont, setCont] = useState(0);

  return (
    <React.Fragment>
      <h2>Contador</h2>
      <button onClick={(cont) => cont + 1}>{cont}</button>
    </React.Fragment>
  );
};

export default Contador;
