import React from 'react';

function Botton() {
  const [cont, setCont] = React.useState(0);

  return (
    <div>
      <input type="text" onChange={this.setState({ username: event.value })} />
      <button
        style={{ backgroundColor: 'green' }}
        onClick={() => {
          setCont(cont + 1);
        }}
      >
        {cont}
      </button>
    </div>
  );
}

export default Botton;
