import React from 'react';
// FORMULÁRIOS, Componentes Input
/*Input
Podemos definir um componente para cada tipo de campo de formulário, assim evitamos criar código repetido.*/

const App = () => {
  const [nome, setNome] = React.useState('');

  return (
    <form>
      <Input
        label="Nome"
        id="nome"
        type="text"
        value={nome}
        setValue={setNome}
      />
    </form>
  );
};

export default App;
