import { useNavigate } from 'react-router-dom';
/*
useNavigate
O hook useNavigate permite navegarmos programaticamente entre as rotas. Por exemplo, pode ser utilizado quando o usu치rio faz um login bem sucedido e enviamos o mesmo a p치gina da sua conta.
*/

const Login = () => {
  const navigate = useNavigate();

  function handleClick() {
    console.log('Faz o login');
    navigate('/sobre');
  }

  return (
    <div>
      <button onClick={handleClick}>Login</button>
    </div>
  );
};

export default Login;
